mapper.select:
      SELECT DISTINCT REC_NO, MEM_NO, OPEN_CHECK FROM INTERESTEDRECRUITMENT
 			
              WHERE MEM_NO=#{memNo}
             
               
               AND REC_NO=#{rec_no}

SELECT E.*,ENT_NAME ,REC_CATEGORY, REP_NAME, REP_PHONE,ENT_LOGO FROM RECRUITMENT E JOIN REVIEW S ON (S.ENT_NO = E.ENT_NO) JOIN ENTERPRISE R ON (R.ENT_NO = E.ENT_NO)
WHERE REC_CAREER <= #{carNum}
AND REC_CATEGORY=#{anoNum}
AND REC_TYPE LIKE '%'||#{employType}||'%'
